import "./App.css";

import { useDispatch, useSelector } from "react-redux";
import { increment, decrement, reset } from "./reducers/user/user";
import { useState } from "react";

function App() {
  const [user, setUser] = useState();
  const [id, setId] = useState(0);

  const dispatch = useDispatch();

  const state = useSelector((state) => {
    return {
      // user: state.user.user,
      user: state.user.user,
    };
  });

  const add = () => {
    setId(id + 1);

    const newUser = {
      id: id + 1,
      name: user,
    };

    const action = addUser(1);

    // {
    //   type: "ADD_USER",
    //   payload: {
    //     id: 2,
    //     name: "Jane"
    //   },
    // }

    dispatch(action);
  };

  return (
    <div className="App">
      <p> name: {state.user.name}</p>
      <p> id: {state.user.id}</p>

      <input
        type="text"
        onChange={(e) => {
          setUser(e.target.value);
        }}
      />
      {/* <button onClick={add}>increment</button> */}
      <button
        onClick={() => {
          dispatch(increment());
        }}
      >
        increment
      </button>
      <button
        onClick={() => {
          dispatch(decrement());
        }}
      >
        decrement
      </button>

      <button
        onClick={() => {
          dispatch(reset());
        }}
      >
      reset
      </button>
    </div>
  );
}

export default App;
