const initialState = {
  user: {},
};

const user = (state = initialState, { type, payload }) => {
  switch (type) {
    case "ADD_USER":
      return {
        user: payload,
      };
    case "CLEAR_USER":
      return {
        user: {},
      };
    case "UPDATE_USER_NAME":
      return {
          user: {
              id: state.user.id,
              name: payload
          }
      }

    default:
      return state;
  }
};

export default user;

export const addUser = (user) => {
  return {
    type: "ADD_USER",
    payload: user,
  };
};

export const clearUser = () => {
  return {
    type: "CLEAR_USER",
  };
};

export const updateUserName = (name) => {
  return {
    type: "UPDATE_USER_NAME",
    payload: name,
  };
};
