import "./App.css";

import { useDispatch, useSelector } from "react-redux";
import { addUser, clearUser, updateUserName } from "./reducers/user/user";
import { useState } from "react";

function App() {
  const [user, setUser] = useState();
  const [id, setId] = useState(0);

  const dispatch = useDispatch();

  const state = useSelector((state) => {
    return {
      user: state.user.user,
    };
  });

  const add = () => {
    setId(id + 1);

    const newUser = {
      id: id + 1,
      name: user,
    };

    const action = addUser(newUser);

    // {
    //   type: "ADD_USER",
    //   payload: {
    //     id: 2,
    //     name: "Jane"
    //   },
    // }

    dispatch(action);
  };

  return (
    <div className="App">
      <p> name: {state.user.name}</p>
      <p> id: {state.user.id}</p>

      <input
        type="text"
        onChange={(e) => {
          setUser(e.target.value);
        }}
      />
      <button onClick={add}>Add</button>
      <button
        onClick={() => {
          setId(0);
          dispatch(clearUser());
        }}
      >
        Clear
      </button>
      <button
        onClick={() => {
          dispatch(updateUserName(user));
        }}
      >
        Update
      </button>
    </div>
  );
}

export default App;
