const initialState = {
  counter:[]
};

const counter = (state = initialState, {type ,payload}) => {
  switch (type) {

    case "ADD_USER":
      return {
        counter: payload,
      };
      case "CLEAR_USER":
      return {
        counter: {},
      };
          case "reset":
              return {
                counter: 0,
              };



    default:
      return state;
  }
};

export default counter;



export const addUser = (user) => {
  return {
    type: "ADD_USER",
    payload: user,
  };
};

export const clearUser = () => {
  return {
    type: "CLEAR_USER",
  };
};
