const initialState = {
  counter:[]
};

const counter = (state = initialState, {type ,payload}) => {
  switch (type) {

    case "ADD_USER":
      return {
        counter: payload,
      };
      case "CLEAR_USER":
      return {
        counter: state.counter.filter(),
      };
      // return newArray.filter((element, i)=>{
      //   //   return index !== i
      //   // })
    default:
      return state;
  }
};

export default counter;



export const addUser = (data) => {
  return {
    type: "ADD_USER",
    payload: data,
  };
};

export const clearUser = () => {
  return {
    type: "CLEAR_USER",
  };
};
